// Snake body segments forming a loose coil
module snake_body() {
    // Tail end - thin cylinder
    translate([40, 0, 0])
        rotate([0, 90, 0])
            cylinder(h=15, d1=3, d2=5, $fn=8);

    // Main body coil
    for(i=[0:15]) {
        // Calculate spiral positions
        angle = i * 30;
        radius = 30 - i * 1.5;

        translate([radius * cos(angle), radius * sin(angle), 0])
            rotate([0, 0, angle + 90])
                cylinder(h=12, d1=6 + i*0.3, d2=6 + (i+1)*0.3, $fn=12);
    }
}

// Snake head with triangular shape
module snake_head() {
    // Main head shape
    translate([0, 0, 3])
        rotate([0, 0, -30])
            linear_extrude(height=8)
                polygon(points=[[0,0], [-8,-4], [-8,4]]);

    // Snout
    translate([-8, 0, 7])
        sphere(d=5, $fn=12);

    // Eyes
    translate([-5, 3.5, 5])
        sphere(d=1.5, $fn=8);
    translate([-5, -3.5, 5])
        sphere(d=1.5, $fn=8);

    // Heat sensing pits
    translate([-6, 2, 2])
        sphere(d=1, $fn=6);
    translate([-6, -2, 2])
        sphere(d=1, $fn=6);
}

