module bands() {
    difference() {
        snake_body();

        // Carve out pattern bands
        for(i=[1:3:15]) {
            angle = i * 30;
            radius = 30 - i * 1.5;

            translate([radius * cos(angle), radius * sin(angle), -2])
                rotate([0, 0, angle + 90])
                    cube([15, 8, 14], center=true);
        }
    }
}

