// Copperhead Snake - Realistic Style, Medium Complexity
// Overall length ~80mm, designed for 3D printing

// Snake body parameters
body_length = 75;
body_segments = 30;
segment_length = body_length / body_segments;
base_diameter = 8;
tail_taper = 0.3;

// Head parameters
head_length = 12;
head_width = 9;
head_height = 7;
snout_length = 4;

// Eye parameters
eye_radius = 1.5;
eye_offset = 3.5;

// Scale texture
scale_size = 1.2;
scale_depth = 0.3;

// Create the complete copperhead
module copperhead() {
    union() {
        // Main body
        snake_body();

        // Head
        snake_head();

        // Eyes
        eyes();

        // Rattle segments (simplified)
        rattle();
    }
}

// Generate the snake body with taper
module snake_body() {
    for(i = [0:body_segments-1]) {
        translate([i * segment_length, 0, 0]) {
            // Calculate diameter based on position
            progress = i / body_segments;
            current_diameter = base_diameter * (1 - progress * tail_taper);

            // Create body segment
            rotate([0, 90, 0]) 
                cylinder(h=segment_length, d=current_diameter, $fn=16);

            // Add scale texture rings
            scale_ring(current_diameter);
        }
    }
}

// Add scale texture to body segments
module scale_ring(diameter) {
    for(j = [0:5]) {
        rotate([0, 0, j * 60]) {
            translate([segment_length/2, diameter/2 - scale_depth, 0])
                scale([scale_size, scale_depth, scale_size])
                    sphere(r=1, $fn=6);
        }
    }
}

// Create the distinct copperhead-shaped head
module snake_head() {
    translate([-head_length/2, 0, 0]) {
        // Main head shape
        scale([1, 0.8, 0.7])
            sphere(r=head_width/2, $fn=32);

        // Snout extension
        translate([head_length/2, 0, 0])
            scale([1.5, 0.6, 0.5])
                sphere(r=snout_length, $fn=16);

        // Add distinctive copperhead ridge
        translate([-head_length/3, 0, head_height/2])
            scale([1.2, 0.4, 0.3])
