// Mobile tower crane, 3D-printable, ~75 mm tall
// All critical angles ≤ 45°, walls ≥ 1.2 mm

$fn=32;

module cab() {
    // Main cab body
    difference() {
        cube([22,15,14], center=true);
        translate([0,0,7]) cube([20,13,1], center=true);    // roof inset
        translate([-10,0,0]) rotate([0,90,0]) cylinder(h=16,d=3); // window left
        translate([10,0,0]) rotate([0,90,0]) cylinder(h=16,d=3);  // window right
    }
    // Cab roof with slight slope (printable)
    translate([0,0,7.5]) cube([22,15,2], center=true);
}

module tower() {
    // Base frame to avoid overhangs
    translate([0,0,-1]) cube([28,28,2], center=true);

    // Four legs as simple cylinders
    for (x=[-10,10])
        for (y=[-10,10])
            translate([x,y,0]) cylinder(h=60,d=4);

    // Cross bracing plates (printable, no overhang)
    translate([0,0,15]) cube([20,2,30], center=true);
    translate([0,0,15]) cube([2,20,30], center=true);
}

module boom() {
    // Main boom truss (triangular cross-section)
    translate([0,0,60]) rotate([0,-30,0]) union() {
        // Bottom chord
        translate([0,-4,0]) cube([40,2,2], center=true);
        // Top chords
        translate([0,2,3.5]) cube([40,2,2], center=true);
        // Vertical struts every 8 mm
        for (i=[-20:8:20])
            translate([i,0,1.75]) cube([2,2,3.5], center=true);
        // Diagonal braces (45° printable)
        for (i=[-16:8:16])
            translate([i,-1,1]) rotate([0,0,45]) cube([2.8,2,2], center=true);
    }
}

module counter_jib() {
    // Short rear arm with counterweight
    translate([0,0,60]) rotate([0,30,180]) union() {
        cube([20,3,3], center=true);                // arm
        translate([-10,0,0]) cube([6,6,6], center=true); // weight block
    }
}

module hook_block() {
    // Simple hook assembly
    translate([0,0,45]) rotate([0,-30,0]) union() {
        translate([20,0,0]) cylinder(h=8,d=2);      // cable
        translate([20,0,-4]) sphere(d=5);           // block
        translate([20,0,-7]) rotate([0,90,0]) cylinder(h=8,d=1.5); // hook
    }
}

// Assemble crane
union() {
    tower();
    translate([0,0,60]) rotate([0,0,45]) cab();
    boom();
    counter_jib();
    hook_block();
}

