// Civet - small nocturnal mammal with cat-like face, long body and ringed tail
// Reference: https://en.wikipedia.org/wiki/Civet

// Overall proportions
body_len = 60;
body_dia = 25;
head_len = 30;
head_dia = 22;
leg_len = 18;
leg_dia = 6;
tail_len = 50;
tail_dia = 5;

// Body – elongated cylinder with slight taper
translate([0, 0, leg_len])
    cylinder(h = body_len, r1 = body_dia/2, r2 = body_dia/2 - 2, center = true);

// Head – slightly flattened sphere attached at front
translate([-(body_len/2 + head_len/2 - 5), 0, leg_len + 5])
    scale([1, 1, 0.8])
    sphere(d = head_dia);

// Snout extension
translate([-(body_len/2 + head_len + 2), 0, leg_len + 5])
    rotate([0, 30, 0])
    cylinder(h = 10, r1 = 4, r2 = 2);

// Ears (small rounded ears on top of head)
translate([-(body_len/2 + head_len/2 - 2), -head_dia/3, leg_len + head_dia/2])
    rotate([90, 0, 0])
    cylinder(h = 6, r1 = 3, r2 = 1);

translate([-(body_len/2 + head_len/2 - 2), head_dia/3, leg_len + head_dia/2])
    rotate([90, 0, 0])
    cylinder(h = 6, r1 = 3, r2 = 1);

// Eyes
translate([-(body_len/2 + head_len/2 + 5), -head_dia/4, leg_len + 8])
    sphere(d = 3);

translate([-(body_len/2 + head_len/2 + 5), head_dia/4, leg_len + 8])
    sphere(d = 3);

// Legs – four simple cylinders
// Front left
translate([-(body_len/2 - 5), -body_dia/2 - leg_dia/2, leg_len/2])
    cylinder(h = leg_len, r = leg_dia/2);

// Front right
translate([-(body_len/2 - 5), body_dia/2 + leg_dia/2, leg_len/2])
    cylinder(h = leg_len, r = leg_dia/2);

// Rear left
translate([body_len/2 - 5, -body_dia/2 - leg_dia/2, leg_len/2])
    cylinder(h = leg_len, r = leg_dia/2);

// Rear right
