module valve() {
    difference() {
        // Main heart-shaped shell body
        scale([1, 0.9, 1])
        sphere(d = shell_w, $fn = 50);

        // Flatten bottom
        translate([0, 0, -shell_h/2 - 1])
            cube([shell_w*2, shell_w*2, shell_h], center = true);

