    // Add radial ribs
    for (a = [0 : 360/rib_count : 359]) {
        rotate([0, 0, a])
        translate([0, -shell_w/2, 0])
            scale([1, 0.3, 0.3])
            cylinder(h = shell_w/2 + 2, r = rib_h, $fn = 8);
    }
}

