// Medium-Complexity Realistic Dog – OpenSCAD
// Body dimensions chosen for 80 mm nose-to-tail length

$fn = 36;          // Good balance between smoothness and printability
wall = 2;          // 2 mm shell for FDM printing

module roundedBox(s, r){
    // Utility: oblong capsule for torso/limbs
    hull(){
        translate([ r,  r,  r]) sphere(r);
        translate([s.x-r,  r,  r]) sphere(r);
        translate([ r, s.y-r,  r]) sphere(r);
        translate([s.x-r, s.y-r,  r]) sphere(r);
        translate([ r,  r, s.z-r]) sphere(r);
        translate([s.x-r,  r, s.z-r]) sphere(r);
        translate([ r, s.y-r, s.z-r]) sphere(r);
        translate([s.x-r, s.y-r, s.z-r]) sphere(r);
    }
}

module ear(){
    // Droopy ear with slight curl
    difference(){
        hull(){
            sphere(5);
            translate([0,-12,0]) sphere(4);
            translate([0,-6,-8]) sphere(3);
        }
        translate([0,-6,-8]) cube([20,20,8], center=true);  // Remove inner
    }
}

module leg(){
    // Simple straight leg with paw
    union(){
        cylinder(h=20, r=4);
        translate([0,0,-2]) sphere(5);  // Paw
    }
}

module tail(){
    // Curled tail using hull of spheres
    hull(){
        sphere(4);
        translate([0,-8,5]) sphere(3);
        translate([4,-12,8]) sphere(2.5);
        translate([8,-14,6]) sphere(2);
    }
}

module body(){
    difference(){
        roundedBox([35,15,20], 8);      // Main torso
        translate([10,0,10]) cube([20,20,20], center=true); // Hollow
    }
}

module head(){
    difference(){
        hull(){
            sphere(9);                  // Skull
            translate([7,0,0]) sphere(7); // Snout
        }
        // Eye sockets
        for(side=[-1,1])
            translate([5, side*6, 3])
                rotate([0,90,0])
                    cylinder(h=10, r=3.5);
        // Nostrils
        for(side=[-1,1])
            translate([11, side*2, -2])
                sphere(1.5);
    }
    // Eyes
    color("black")
    for(side=[-1,1])
        translate([5.5, side*6, 2.5])
            sphere(2);
    // Ears
    translate([0, 9, 5]) rotate([0,-45,0]) ear();
    translate([0,-9, 5]) mirror([0,1,0]) rotate([0,-45,0]) ear();
}

// Assembly
union(){
    // Body
    translate([0,0,20]) body();

    // Head
    translate([22,0,28]) rotate([0,-10,0]) head();

    // Legs
    translate([ 8, 6,0]) leg();
    translate([ 8,-6,0]) leg();
    translate([-8, 6,0]) leg();
    translate([-8,-6,0]) leg();

    // Tail
    translate([-20,0,20]) rotate([0,0,-30]) tail();
}

