// Cuttlefish - Realistic style, medium complexity
// Body length ~80mm, printable without supports

$fn = 48;
body_length = 80;
mantle_thickness = 3;
fin_thickness = 2;

module mantle() {
    // Main mantle body - elongated oval
    difference() {
        scale([1, 0.7, 1.8]) 
            sphere(d=body_length * 0.6);

        // Hollow interior for printing
        translate([0, 0, -5])
            scale([0.8, 0.5, 1.6])
                sphere(d=body_length * 0.6 - mantle_thickness*2);
    }
}

module head() {
    // Head section with eyes
    translate([0, 0, -body_length * 0.5]) {
        // Head bulb
        scale([0.8, 0.9, 0.6])
            sphere(d=body_length * 0.4);

        // Eye sockets
        for(side = [-1, 1]) {
            translate([side * body_length * 0.15, 0, -body_length * 0.15]) {
                rotate([0, 90, 0])
                    cylinder(h=body_length * 0.3, d=body_length * 0.12);
            }
        }
    }
}

module eyes() {
    // Eye spheres
    for(side = [-1, 1]) {
        translate([side * body_length * 0.15, 0, -body_length * 0.15]) {
            sphere(d=body_length * 0.1);
        }
    }
}

module arms() {
    // 8 arms in pairs
    arm_length = body_length * 0.5;

    for(pair = [0:3]) {
        angle = pair * 45 + 22.5;
        for(side = [-1, 1]) {
            rotate([0, 0, side * angle])
                translate([0, -body_length * 0.2, -body_length * 0.7]) {
                    // Base of arm
                    rotate([90, 0, 0])
                        cylinder(h=arm_length * 0.3, d=arm_length * 0.12);

                    // Suckers along arm
                    for(i = [1:4]) {
                        translate([0, 0, -i * arm_length * 0.1])
                            scale([1, 1, 0.3])
                                sphere(d=arm_length * 0.08);
                    }
                }
        }
    }

    // Two longer tentacles
    tentacle_length = body_length * 0.8;
    for(side = [-1, 1]) {
        rotate([0, 0, side * 15])
            translate([side * body_length * 0.1, -body_length * 0.2, -body_length * 0.75]) {
                rotate([90, 0, 0])
                    cylinder(h=tentacle_length * 0.4, d=arm_length * 0.08);

                // Tentacle clubs
                translate([0, 0, -tentacle_length * 0.4])
                    sphere(d=arm_length * 0.15);
            }
    }
}

module fins() {
    // Side fins
    fin_width = body_length * 0.4;
    fin_height = body_length * 0.3;

    for(side = [-1, 1]) {
        translate([side * body_length * 0.25, 0, body_length * 0.3]) {
            rotate([0, side * 15, 0])
                scale([0.3, 1, 1])
                    difference() {
                        sphere(d=fin_width);

                        // Thin the fin
                        translate([0, 0, -fin_height * 0.3])
                            scale([1.2, 1.2, 0.5])
                                sphere(d=fin_width);
                    }
        }
    }
}

module siphon() {
    // Funnel/siphon underneath
    translate([0, body_length * 0.25, -body_length * 0.3]) {
        rotate([0, 0, 0])
            cylinder(h=body_length * 0.2, d1=body_length * 0.15, d2=body_length * 0.08);
    }
}

union() {
    mantle();
    head();
    eyes();
    arms();
    fins();
    siphon();
}

