// Approx 80mm wingspan, 3D printable with no overhangs

$fn = 32;

module dove() {
    // Body - main oval shape
    union() {
        // Main body - elongated sphere
        translate([0, 0, 10])
            scale([1.5, 1, 1])
            sphere(15);

        // Head - smaller sphere
        translate([20, 0, 22])
            sphere(8);

        // Neck - connecting cylinder
        translate([12, 0, 18])
            rotate([0, -20, 0])
            cylinder(h=10, r1=7, r2=5);

        // Tail - tapered shape
        difference() {
            translate([-20, 0, 12])
                rotate([0, 15, 0])
                scale([2, 0.6, 0.3])
                sphere(12);

            // Cut tail to be flat
            translate([-35, 0, 0])
                cube([30, 30, 30]);
        }

        // Wings - symmetrical pair
        wing();

        // Feet - simple cylinders
        translate([5, -3, 0])
            cylinder(h=5, r=1.5);
        translate([5, 3, 0])
            cylinder(h=5, r=1.5);

        // Beak - small cone
        translate([28, 0, 22])
            rotate([0, -20, 0])
            cylinder(h=5, r1=3, r2=0.5);

        // Eyes - tiny spheres
        translate([22, -3, 24])
            sphere(1);
        translate([22, 3, 24])
            sphere(1);
    }
}

module wing() {
    // Left wing
    translate([0, 15, 18])
        rotate([0, 0, -30])
        rotate([15, 0, 0])
        wing_shape();

    // Right wing - mirror of left
    mirror([0, 1, 0])
        translate([0, 15, 18])
            rotate([0, 0, -30])
            rotate([15, 0, 0])
            wing_shape();
}

