                rotate([0, -10, 0]) {
                    scale([1.5, 0.5, 0.4]) {
                        difference() {
                            sphere(d=12, $fn=8);
                            translate([-6, 0, 0]) cube([12, 8, 8], center=true);
                        }
                    }
                }
            }

            // Eyes
            // Left eye
            translate([35, -7, 38]) {
                sphere(d=4, $fn=8);
            }

            // Right eye
            translate([35, 7, 38]) {
                sphere(d=4, $fn=8);
            }
        }

        // Legs
        module leg(side) {
            translate([10, side * 8, 0]) {
                union() {
                    // Upper leg
                    translate([0, 0, 5]) {
                        rotate([0, -10, side * 20]) {
                            cylinder(h=10, d=6, $fn=12);
                        }
                    }

                    // Lower leg
                    translate([2, side * 2, -2]) {
                        rotate([0, 0, side * 20]) {
