// Drongo bird - medium complexity realistic style
// Designed for 3D printing with proper wall thickness

$fn = 32; // Smooth curves
body_length = 50;
wing_span = 70;
tail_length = 60;
head_size = 12;

module main_drongo() {
    union() {
        // Main body
        body();

        // Head
        translate([body_length/2 - 5, 0, 8])
            head();

        // Wings
        translate([-body_length/4, wing_span/2 - 5, 0])
            rotate([0, 0, 20])
            wing();
        translate([-body_length/4, -(wing_span/2 - 5), 0])
            rotate([0, 0, -20])
            mirror([0, 1, 0])
            wing();

        // Tail
        translate([-body_length/2 - tail_length/3, 0, 0])
            tail();

        // Legs
        translate([body_length/4, 5, -8])
            leg();
        translate([body_length/4, -5, -8])
            leg();
    }
}

module body() {
    // Main oval body
    scale([1.5, 1, 1])
    rotate([90, 0, 0])
