            }
        }

        // Hollow out body
        translate([0,0,6])
            scale([1-0.1,0.7-0.1,0.5-0.1])
            sphere(d=20-2*shell, $fn=30);

        for(i=[1:5]) {
            translate([0,-8-5*i,3])
                scale([1-0.1*i-0.1,0.7-0.05*i-0.1,0.4-0.1])
                sphere(d=12-2*i-2*shell, $fn=20);
        }
    }
}

