    translate([6,5,12])
        rotate([0,20,0])
        cylinder(h=6, d1=3, d2=2);
    translate([6,5,18])
        sphere(d=2.5);
}

// Leg segment
module leg_segment(len, dia, angle) {
    rotate([0,0,angle])
        translate([len/2,0,0])
            rotate([0,90,0])
                cylinder(h=len, d=dia, $fn=8);
}

// Walking legs
module legs() {
    for(side=[-1,1]) {
        mirror([side,0,0]) {
            // First pair (claws)
            translate([9,3,5])
                rotate([0,0,30])
                claw();

            // Walking legs
            for(i=[1:3]) {
                translate([8-i*2,3-i*2,5-i])
                    rotate([0,20*i,0])
                    union() {
                        leg_segment(8, 2, -30);
                        translate([8,0,0])
                            rotate([30,0,0])
                            leg_segment(6, 1.5, 0);
                        translate([8+5,0,2])
                            rotate([60,0,0])
                            leg_segment(5, 1, 0);
                    }
            }
        }
    }
}

// Claw
