        // Hollow claws
        translate([4,0,0])
            rotate([90,0,0])
            cylinder(h=10, d1=4-2*shell, d2=2-2*shell, $fn=6);
        mirror([0,0,1]) {
            translate([4,0,0])
                rotate([90,0,0])
                cylinder(h=9, d1=4-2*shell, d2=2-2*shell, $fn=6);
        }
    }
}

// Antennae
module antennae() {
    for(side=[-1,1]) {
        translate([side*4,8,10])
            rotate([45*side,0,15])
            cylinder(h=12, d=0.8, $fn=4);
        translate([side*6,8,10])
            rotate([30*side,0,-15])
            cylinder(h=10, d=0.6, $fn=4);
    }
}

// Assemble crayfish
union() {
    body();
    legs();

    // Eyes and antennae
    eye();
    mirror([1,0,0]) eye();
    antennae();
