        sphere(d=4);
    }
}

module neck() {
    // Neck connecting head to body
    translate([0, 0, 8])
    cylinder(h=10, d1=10, d2=15);
}

module body() {
    // Main body
    translate([0, 0, 0])
    scale([1.5, 1, 1.2])
    sphere(d=30);

    // Tail
    difference() {
        translate([-25, 0, -5])
        rotate([0, 10, 0])
        cube([30, 2, 15]);
        // Tail feather cuts
        for(i = [-1:1]) {
            translate([-25, i*8, -5])
            rotate([0, 10, 0])
            cube([30, 2, 15]);
        }
    }
}

module wing() {
    // Main wing structure
    difference() {
        union() {
            // Upper wing
            translate([0, 0, 0])
            rotate([0, 0, -10])
            scale([1, 2.5, 0.3])
            sphere(d=25);

            // Lower wing
            translate([-5, 15, -5])
            rotate([0, 0, -20])
            scale([0.8, 2, 0.2])
            sphere(d=20);

            // Wing tip
            translate([-10, 35, -8])
            rotate([0, 0, -30])
            cylinder(h=15, d1=8, d2=3);
        }

        // Wing feather cuts
        for(i = [0:3]) {
            translate([0, 5 + i*8, 2])
            rotate([0, 0, -10 - i*5])
            cube([20, 1, 5]);
        }
    }
}

module legs() {
