// Dunlin (Calidris alpina) - medium complexity realistic style
// Size: ~70mm beak to tail

// Basic parameters
$fn = 32;  // Smooth curves
body_scale = 1;
beak_length = 12;
body_length = 35;
wing_span = 40;
leg_height = 8;

// Main body - oval shape
module body() {
    difference() {
        // Main body ellipsoid
        scale([1.2, 0.8, 0.6])
        sphere(d=body_length);

        // Ventral flattening
        translate([0, 0, -body_length*0.25])
        cube([body_length*1.5, body_length, body_length*0.5], center=true);
    }
}

// Head with beak
module head() {
    union() {
        // Head sphere
        translate([-body_length*0.45, 0, body_length*0.3])
        scale([0.8, 0.8, 0.9])
        sphere(d=body_length*0.35);

        // Beak - tapered cylinder
        translate([-body_length*0.6 - beak_length/2, 0, body_length*0.25])
        rotate([0, -10, 0])
        cylinder(h=beak_length, d1=body_length*0.15, d2=2, center=true);
    }
}

// Wing with feather detail
module wing() {
    difference() {
        // Main wing shape
        translate([0, body_length*0.4, 0])
        rotate([0, 0, -20])
        scale([1.3, 0.2, 0.6])
        sphere(d=wing_span/2);

        // Wing tip
        translate([wing_span/2.5, body_length*0.4, 0])
        sphere(d=wing_span/4);
    }

    // Wing feathers - simple ridges
    for(i = [1:3]) {
        translate([i*5-10, body_length*0.4 + i*2, -2])
        rotate([0, 90, 0])
        cylinder(h=0.5, d=wing_span/3, center=true);
    }
}

// Tail
module tail() {
    translate([body_length*0.5, 0, -body_length*0.1])
    rotate([0, -15, 0])
    scale([0.8, 0.2, 0.4])
    sphere(d=body_length*0.3);
}

// Leg with foot
module leg(side) {
    // Upper leg
    translate([body_length*0.2, side*body_length*0.3, -body_length*0.3])
    rotate([30, 0, 0])
    cylinder(h=leg_height, d=2, center=true);

    // Lower leg
    translate([body_length*0.2 + 2, side*body_length*0.3, -body_length*0.3 - leg_height/2])
    rotate([30, 0, 0])
    cylinder(h=leg_height/2, d=1.5, center=true);

    // Webbed foot
    translate([body_length*0.2 + 4, side*body_length*0.3, -body_length*0.3 - leg_height])
    rotate([90, 0, 0])
    scale([1, 0.3, 1])
    sphere(d=6);
}

// Eye detail
module eye() {
    translate([-body_length*0.55, body_length*0.12, body_length*0.35])
    sphere(d=1.5);
}

// Main assembly
union() {
    body();
    head();
    wing();
    mirror([0,1,0]) wing();
    tail();

    // Legs
    leg(1);
    leg(-1);

    // Eyes
    color("black") eye();
    mirror([0,1,0]) color("black") eye();
}

