// Echidna - medium complexity realistic style
// Roughly 70mm long, printable without supports

$fn = 36;

// Overall scale factor
scale_factor = 1.0;

// Main body
module body() {
    // Torso - elongated ellipsoid
    translate([0, 0, 10]) 
        scale([1.3, 0.8, 0.6]) 
            sphere(r = 20);

    // Rump - slightly smaller sphere
    translate([-25, 0, 10]) 
        scale([1.1, 0.9, 0.5]) 
            sphere(r = 15);
}

// Head with elongated snout
module head() {
    // Main head
    translate([28, 0, 15]) 
        scale([0.9, 0.8, 0.7]) 
            sphere(r = 12);

    // Snout/nose
    translate([38, 0, 12]) 
        scale([2.0, 0.5, 0.5]) 
            rotate([0, -15, 0]) 
                cylinder(h = 15, r1 = 8, r2 = 4);

    // Eyes
    translate([30, 6, 18]) sphere(r = 2);
    translate([30, -6, 18]) sphere(r = 2);
}

// Limbs - short with claws
module limb(x_pos, y_pos, z_pos, angle) {
    translate([x_pos, y_pos, z_pos]) {
        rotate([0, 0, angle]) {
            // Upper leg
            rotate([-30, 0, 0]) 
                scale([0.8, 0.8, 1.2]) 
                    cylinder(h = 8, r = 4);

            // Lower leg
            translate([0, 0, -10]) 
                rotate([30, 0, 0]) 
                    cylinder(h = 10, r = 3);

            // Foot/Claws
            translate([0, 0, -18]) {
                // Central claw
                cylinder(h = 5, r = 2);

                // Side claws
                translate([2, 2, 0]) cylinder(h = 4, r = 1.5);
                translate([-2, 2, 0]) cylinder(h = 4, r = 1.5);
            }
        }
    }
}

// Spines covering the body
module spines() {
    // Back spines in rows
    for (i = [-20:5:25]) {
        for (j = [-15:7:15]) {
            if (i*i + j*j < 400) {
                translate([i, j, 30 + sin(i*0.3)*5]) {
                    rotate([90 - (i+20)*2, 0, atan2(j,i)*180/3.14159]) 
                        cylinder(h = 6 + 4*sin(i*0.2), r1 = 1.5, r2 = 0.5);
                }
            }
        }
    }

    // Side spines
    for (i = [-15:5:20]) {
        translate([i, 20, 20]) 
            rotate([60, 0, 90]) 
                cylinder(h = 5, r1 = 1.2, r2 = 0.5);
        translate([i, -20, 20]) 
            rotate([60, 0, -90]) 
                cylinder(h = 5, r1 = 1.2, r2 = 0.5);
    }
}

// Tail
module tail() {
    // Base of tail
    translate([-35, 0, 12]) {
        rotate([0, 20, 0]) {
            cylinder(h = 15, r1 = 8, r2 = 5);

            // Tail extension
            translate([0, 0, -15]) {
                rotate([0, 10, 0]) 
                    cylinder(h = 12, r1 = 5, r2 = 2);
            }
        }
    }
}

// Assembly
scale([scale_factor, scale_factor, scale_factor]) {
    union() {
        body();
        head();
        tail();

        // Four limbs
        limb(15, 18, 5, 30);
        limb(15, -18, 5, -30);
        limb(-5, 15, 5, 120);
        limb(-5, -15, 5, -120);

        spines();
    }
}

