// Eel in realistic style, medium complexity
// Body length ~80mm, suitable for 3D printing

$fn = 32; // Smooth curves

// Main module
module eel() {
    // Body segments - use hull to create smooth body
    color("darkslategray") body();

    // Head
    color("darkslategray") head();

    // Fins
    color("dimgray") dorsal_fin();
    color("dimgray") pectoral_fin();
    color("dimgray") tail_fin();

    // Eyes
    color("black") eye(-4, 3, 2.5);
    color("black") eye(4, 3, 2.5);
}

// Body segments
module body() {
    // Create smooth body by hulling spheres along a curve
    for (i = [0:5:70]) {
        hull() {
            translate([i, 0, 0]) sphere(d = 10 - i/10);
            translate([i+5, 0, 0]) sphere(d = 10 - (i+5)/10);
        }
    }
}

// Head
module head() {
    // Main head shape
    translate([-10, 0, 0]) {
        scale([1.2, 0.8, 0.8]) sphere(d = 12);
    }

    // Snout extension
    translate([-18, 0, 0]) {
        scale([1.5, 0.3, 0.3]) sphere(d = 8);
    }
}

// Dorsal fin
module dorsal_fin() {
    // Fin segments along back
    for (i = [10:10:50]) {
        translate([i, 0, 5 - i/20]) {
            rotate([0, 0, 90]) 
            scale([0.3, 2, 0.1]) 
            cylinder(h = 4 + sin(i*3)*2, d1 = 2, d2 = 0.5);
        }
    }
}

// Pectoral fins
module pectoral_fin() {
    // Left fin
    translate([0, -5, -2]) {
        rotate([0, 20, -30]) 
        scale([1, 0.3, 0.1]) 
        cylinder(h = 8, d1 = 4, d2 = 2);
    }

    // Right fin
    translate([0, 5, -2]) {
        rotate([0, 20, 30]) 
        scale([1, 0.3, 0.1]) 
        cylinder(h = 8, d1 = 4, d2 = 2);
    }
}

// Tail fin
module tail_fin() {
    // Upper tail lobe
    translate([70, 0, 0]) {
        rotate([0, 0, -20]) 
        scale([1, 0.2, 0.1]) 
        cylinder(h = 10, d1 = 5, d2 = 1);
    }

    // Lower tail lobe
    translate([70, 0, 0]) {
        rotate([0, 0, 200]) 
        scale([1, 0.2, 0.1]) 
        cylinder(h = 8, d1 = 5, d2 = 1);
    }
}

// Eye module
module eye(x, y, z) {
    translate([x-10, y, z]) {
        // Eye socket
        difference() {
            sphere(d = 2.5);
            translate([0.5, 0, 0]) sphere(d = 2);
        }

        // Pupil
        translate([1, 0, 0]) sphere(d = 1);
    }
}

// Generate the eel
eel();

