// Dimensions: ~80mm long

$fn = 24;

// Main body
module body() {
    // Torso
    union() {
        hull() {
            translate([-30, 0, 10]) sphere(d=20);
            translate([10, 0, 15]) sphere(d=18);
            translate([30, 0, 12]) sphere(d=15);
        }

        // Neck transition
        hull() {
            translate([35, 0, 15]) sphere(d=12);
            translate([45, 0, 18]) sphere(d=10);
        }
    }
}

// Head
module head() {
    union() {
        // Main head
        hull() {
            translate([50, 0, 20]) sphere(d=12);
            translate([65, 0, 20]) sphere(d=10);
            translate([60, 0, 25]) sphere(d=8);
        }

        // Muzzle
        hull() {
            translate([63, 0, 20]) sphere(d=6);
            translate([70, 0, 20]) sphere(d=4);
        }

        // Ears
        for(side = [-1, 1]) {
            translate([57, side*8, 28]) {
                rotate([0, -20*side, 0]) 
                    cylinder(h=8, d1=6, d2=2);
            }
        }

        // Eyes
        for(side = [-1, 1]) {
            translate([62, side*4, 22]) {
                difference() {
                    sphere(d=3);
                    translate([1, 0, 0]) sphere(d=2.5);
                }
            }
        }

        // Nose
        translate([70, 0, 20]) sphere(d=2);
    }
}

// Tail
module tail() {
    difference() {
        hull() {
            translate([-35, 0, 12]) sphere(d=10);
            translate([-50, 0, 15]) sphere(d=8);
            translate([-65, 0, 12]) sphere(d=6);
            translate([-80, 0, 8]) sphere(d=4);
        }

        // Tail tip
        translate([-78, 0, 8]) sphere(d=6);
    }

    // Tail tip coloring
    translate([-78, 0, 8]) sphere(d=5);
}

// Legs
module legs() {
    for(pos = [0, 1]) {
        // Front legs
        translate([30 - pos*5, pos?-8:8, 0]) {
            union() {
                cylinder(h=15, d=6);
                translate([0, 0, -2]) sphere(d=7);
                // Paw
                translate([0, 0, -4]) {
                    sphere(d=8);
                    for(x = [-2, 2]) {
                        translate([x, pos?-3:3, -2]) sphere(d=2);
