    }
}

module body() {
    // Main torso - elongated sphere
    translate([0, 0, 5])
    scale([1.5, 1, 1.2])
    sphere(15);

    // Chest protrusion
    translate([10, 0, 0])
    scale([1.8, 1.2, 1])
    sphere(12);
}

module head() {
    // Head sphere
    translate([-20, 0, 15])
    sphere(10);

    // Beak - cone
    translate([-32, 0, 15])
    rotate([0, 90, 0])
    cylinder(h1 = 0, h2 = 10, r1 = 0, r2 = 3);

    // Eye sockets
    translate([-22, 5, 17])
    sphere(2.5);

    translate([-22, -5, 17])
    sphere(2.5);

    // Eye balls
    translate([-23.5, 5, 17])
    sphere(1.5);

    translate([-23.5, -5, 17])
    sphere(1.5);
}

module left_wing() {
    translate([0, 15, 10])
    rotate([0, 0, 30])
    difference() {
        // Main wing
        union() {
            // Upper wing
            translate([5, 0, 0])
            scale([2.5, 0.5, 0.8])
            cube([25, 15, 3], center = true);

            // Lower wing
            translate([20, 0, -5])
            rotate([0, 0, -20])
            scale([2, 0.5, 0.6])
            cube([20, 12, 2], center = true);

            // Wing tip
            translate([35, 0, -8])
            rotate([0, 0, -45])
            scale([1.5, 0.4, 0.4])
            cube([10, 8, 1.5], center = true);
        }

        // Wing feather cuts
        for (i = [0:2]) {
            translate([10 + i*10, 0, 0])
            rotate([0, 0, 45])
            cube([3, 20, 5], center = true);
        }
    }
}

module right_wing() {
    mirror([0, 1, 0])
    left_wing();
}

module tail() {
    translate([25, 0, 0])
    rotate([0, 0, 0])
    union() {
        // Tail base
        scale([1, 2, 0.5])
        cube([15, 8, 4], center = true);

        // Tail feathers
        for (i = [-1:1]) {
            translate([30, i*4, 0])
            scale([1.2, 0.8, 0.3])
            cube([8, 6, 2]);
        }
    }
}

module feet() {
    // Left foot
    translate([0, 8, -10])
    union() {
        // Leg
        cylinder(h = 12, r = 2);

        // Foot
        translate([0, 3, -7])
        cube([8, 6, 2]);

        // Toes
        for (i = [-1, 0, 1]) {
            translate([i*2.5, 6, -7])
            cube([2, 4, 2]);
        }
    }

    // Right foot
    translate([0, -8, -10])
    mirror([0, 1, 0])
    union() {
        cylinder(h = 12, r = 2);
        translate([0, 3, -7])
        cube([8, 6, 2]);
        for (i = [-1, 0, 1]) {
            translate([i*2.5, 6, -7])
            cube([2, 4, 2]);
        }
    }
}

// Render the falcon
falcon();

