            // Chest area
            translate([-body_length/4, 0, -body_height/4]) 
                scale([1.2, 0.8, 0.8]) sphere(r=body_width/2, $fn=16);

            // Rump area
            translate([body_length/3, 0, -body_height/6]) 
                scale([1.3, 0.9, 0.9]) sphere(r=body_width/2, $fn=16);
        }

