            sphere(r=tail_radius, $fn=12);

        translate([body_length/2 + tail_length/3, -tail_length/4, -body_height/4 - 2]) 
            sphere(r=tail_radius*0.9, $fn=12);

        translate([body_length/2 + tail_length, -tail_length/2, -body_height/4 - 4]) 
            sphere(r=tail_radius*0.7, $fn=12);
