// Eland - Medium complexity realistic style
// Designed for 3D printing with wall thickness >= 2mm

module body() {
    // Main torso - elongated ellipsoid
    translate([0, 0, 15]) {
        scale([1.5, 0.8, 1]) 
            sphere(r=15, $fn=32);
    }

    // Rump - slightly larger sphere
    translate([-20, 0, 15]) {
        scale([1.2, 1, 0.9])
            sphere(r=12, $fn=32);
    }
}

module neck() {
    // Neck - tapered cylinder
    translate([12, 0, 25]) {
        rotate([30, 0, 0]) {
            cylinder(h1=10, h2=15, r1=8, r2=6, $fn=16);
        }
    }
}

module head() {
    // Head - elongated box with rounded end
    translate([25, 0, 35]) {
        rotate([30, 0, 0]) {
            // Main head shape
            scale([1.2, 0.6, 0.8])
                sphere(r=10, $fn=32);

            // Muzzle extension
            translate([8, 0, -2]) {
                scale([1.5, 0.8, 0.7])
                    sphere(r=6, $fn=16);
            }

            // Horns
            for(side = [-1, 1]) {
                translate([0, side*6, 5]) {
                    rotate([0, side*10, 0]) {
                        cylinder(h=15, r1=2, r2=1, $fn=8);
                    }
                }
            }

            // Ears
            for(side = [-1, 1]) {
                translate([2, side*8, 3]) {
                    rotate([0, 0, side*20]) {
                        scale([0.5, 1, 2])
                            sphere(r=3, $fn=8);
                    }
                }
            }
        }
    }
}

module legs() {
    // Generate four legs with slight positioning variation
    for(pos = [[8, 10], [8, -10], [-12, 10], [-12, -10]]) {
        translate([pos[0], pos[1], 12]) {
            // Upper leg
            cylinder(h=20, r=3, $fn=12);

            // Lower leg
            translate([0, 0, -20]) {
                cylinder(h=18, r=2.5, $fn=12);

                // Hoof
                translate([0, 0, -18]) {
                    scale([1.2, 1.2, 0.5])
                        sphere(r=2.5, $fn=8);
                }
            }
        }
    }
}

module tail() {
    // Tail with tuft
    translate([-25, 0, 15]) {
        rotate([45, 0, 0]) {
            // Tail body
            cylinder(h=15, r=1.5, $fn=8);

            // Tuft
            translate([0, 0, -15]) {
                sphere(r=3, $fn=16);
            }
        }
    }
}

module dewlap() {
    // Hanging skin flap under neck
    translate([15, 0, 15]) {
        scale([1, 0.3, 2])
            sphere(r=8, $fn=16);
    }
}

module stripes() {
    // Vertical body stripes (simplified as raised ridges)
    for(i = [-15:5:15]) {
        translate([i, 0, 15]) {
            scale([0.5, 0.9, 0.1])
                sphere(r=15, $fn=32);
        }
    }
}

// Main assembly
union() {
    body();
    neck();
    head();
    legs();
    tail();
    dewlap();

    // Add subtle stripes
    stripes();
}

// Add support base for 3D printing
