// Realistic 3D-printable fish – OpenSCAD
// Approx. 85 mm long, 50 mm tall, 25 mm thick
// Wall thickness ≥ 2 mm, no severe overhangs

$fn = 40;

// overall scale – change only this
scale_factor = 1;

module fish() {
    scale([scale_factor,scale_factor,scale_factor])
    union() {

        // ---------- BODY ----------
        // central lozenge
        translate([0,0,0])
        scale([30,12,15])
        sphere(1);

        // tail root (widening)
        translate([-25,0,0])
        scale([15,10,12])
        sphere(1);

        // dorsal arch
        translate([0,0,10])
        scale([22,8,7])
        sphere(1);

        // ---------- TAIL ----------
        module tail_fin() {
            difference() {
                // main fin
                translate([-35,0,0])
                rotate([0,-10,0])
                scale([20,16,2])
                cube([1,1,1], center=true);

                // trim trailing edge
                translate([-45,0,-1])
                rotate([0,0,30])
                cube([25,25,4], center=true);
            }
        }
        tail_fin();

        // ---------- DORSAL FIN ----------
        module dorsal_fin() {
            for(i = [-1:1]) {
                translate([i*8,0,18])
                rotate([0,0,i*10])
                scale([6,4,8])
                cylinder(h=1, r1=0.5, r2=0);
            }
            // central spine
            translate([0,0,18])
            rotate([-10,0,0])
            scale([20,3,10])
            cube([1,1,1], center=true);
        }
        dorsal_fin();

        // ---------- PECTORAL FINS ----------
        module pectoral_fin(side) {
            mirror([side,0,0])
            translate([2,side*12,-3])
            rotate([0,0,side*20])
            rotate([0,-20,0])
            scale([8,12,1.5])
            cube([1,1,1], center=true);
        }
        pectoral_fin(1);
        pectoral_fin(-1);

        // ---------- HEAD ----------
        // snout
        translate([20,0,0])
        scale([12,10,10])
        sphere(1);

        // lower jaw
        translate([18,0,-7])
        scale([8,6,4])
        sphere(1);

        // ---------- EYE ----------
        translate([22,6,3])
        difference() {
            sphere(3.5);          // outer white
            translate([0,0,-0.5])
            sphere(2.5);          // pupil
        }

        // ---------- GILL SLITS ----------
        for(i = [1:3]) {
            translate([10-i*3,7,-2])
            rotate([0,90,0])
            scale([0.5,2.5,2.5])
            cylinder(h=1, r1=1, r2=0);
        }

    }
}

