// Flamingo - Realistic Medium Complexity
// Body, neck, head, beak, legs, and feet with 3D-printable geometry

// Configuration
body_scale = 1;
neck_length = 35;
leg_height = 40;
foot_length = 15;
wall = 1.6;

// Colors (for preview only)
color_body = "#FFB6C1";
color_beak = "#000000";

// Main flamingo assembly
module flamingo() {
    union() {
        // Body
        color(color_body) body();

        // Neck and head
        translate([0, 0, 12]) neck_and_head();

        // Legs
        translate([8, 0, -leg_height]) right_leg();
        translate([-8, 0, -leg_height]) left_leg();
    }
}

// Body - elongated ellipsoid
module body() {
    scale([1.2, 0.6, 0.7])
        sphere(d=30);
}

// Neck and head assembly
module neck_and_head() {
    rotate([30, 0, 0])
    union() {
        // Neck - series of tapered cylinders
        for(i = [0:5]) {
            translate([0, 0, i*5])
                rotate([0, 0, sin(i*15)*5])
                    cylinder(h=6, d1=8-i, d2=7-i, center=true);
        }

        // Head - small sphere
        translate([0, 0, 35])
            sphere(d=10);

        // Beak
        translate([0, 7, 37])
            rotate([-30, 0, 0])
                cylinder(h=15, d1=3, d2=1);

        // Beak tip black
        color(color_beak)
            translate([0, 14.5, 39])
                sphere(d=2);
    }
}

// Right leg
module right_leg() {
    union() {
        // Upper leg
        cylinder(h=20, d=4);

        // Knee joint
        translate([0, 0, 10])
            sphere(d=5);

        // Lower leg
        translate([3, 0, 20])
            rotate([30, 0, 0])
                cylinder(h=20, d=3);

        // Foot
        translate([6, 0, 38])
            rotate([90, 0, 0])
                foot();
    }
}

// Left leg - mirror of right
module left_leg() {
    mirror([1, 0, 0])
        right_leg();
}

// Webbed foot
module foot() {
    difference() {
        union() {
            // Main foot pad
            scale([1, 0.3, 0.2])
                sphere(d=foot_length);

            // Toes
            for(i = [-1:1]) {
                translate([i*4, 0, 0])
                    rotate([0, 0, i*10])
                        scale([0.3, 0.15, 0.1])
                            sphere(d=foot_length);
            }
        }

        // Remove bottom for flat print
        translate([0, 0, -2])
            cube([20, 10, 4], center=true);
    }
}

// Generate the flamingo
flamingo();

