                            scale([0.5, 0.5, 1])
                                sphere(r=2, $fn=8);
                    }
                }
            }
        }
    }

    // Front legs
    leg([15, 8, -15], [0, 0, 30]);
    leg([15, -8, -15], [0, 0, -30]);

    // Rear legs
    leg([-15, 8, -12], [0, 0, -30]);
    leg([-15, -8, -12], [0, 0, 30]);

    // Tail
    translate([-30, 0, 0]) {
        rotate([0, 20, 0]) {
            scale([0.8, 0.8, 1])
                cylinder(h=20, r1=6, r2=3, $fn=16);
            translate([0, 0, 20])
                sphere(r=3, $fn=16);
        }
    }
}

