// Realistic 3D-printable flea
// Overall length ~70 mm

module flea() {
    // Thorax-abdomen block
    color("brown")
    union() {
        // Thorax
        translate([0, 0, 6])
            rotate([0, 90, 0])
                scale([1.2, 1.2, 1.5])
                    cylinder(h = 12, r1 = 4, r2 = 3, $fn = 32);

        // Abdomen
        translate([0, -10, 1])
            rotate([0, 90, 0])
                scale([1, 1, 1.2])
                    cylinder(h = 20, r1 = 3, r2 = 5, $fn = 32);

        // Head
        translate([12, 0, 3])
            sphere(d = 6, $fn = 32);
    }

    // Antennae
    for (side = [-1, 1]) {
        translate([11, side * 1.5, 6])
            rotate([0, 30, side * 15])
                cylinder(h = 6, r = 0.4, $fn = 8);
    }

    // Eyes
    color("black")
    for (side = [-1, 1]) {
        translate([12.5, side * 2, 4])
            sphere(d = 1.2, $fn = 12);
    }

    // Mouth parts
    difference() {
        translate([14, 0, 3])
            cylinder(h = 2, r = 1, $fn = 12);
        translate([14.5, 0, 3])
            cylinder(h = 2.2, r = 0.5, $fn = 12);
    }

    // Legs
    module leg(angle, y) {
        translate([0, y, 5])
            rotate([0, angle, 0])
                union() {
                    // Coxa
                    translate([5, 0, 0])
                        rotate([0, 90, 0])
                            cylinder(h = 4, r = 0.8, $fn = 8);

                    // Femur
                    translate([8, 0, -2])
                        rotate([45, 0, 0])
                            cylinder(h = 6, r = 0.6, $fn = 8);

                    // Tibia
                    translate([8, 0, -6])
                        rotate([90, 0, 0])
                            cylinder(h = 5, r = 0.4, $fn = 8);

                    // Tarsi
                    translate([8, 0, -9.5])
                        rotate([110, 0, 0])
                            cylinder(h = 3, r = 0.3, $fn = 8);
                }
    }

    // 3 pairs of legs
    leg(0, 2);
    leg(0, -2);
    leg(0, 0);
    leg(180, 4);
    leg(180, -4);
    leg(180, 0);

    // Jumping hind legs with enlarged femora
    module hind_leg(side) {
        translate([-8, side * 3, 3])
            rotate([0, -30, side * 15])
                union() {
                    // Coxa
                    cylinder(h = 5, r = 1, $fn = 8);

                    // Enlarged femur for jumping
                    translate([0, 0, -5])
                        rotate([45, 0, 0])
                            scale([1, 1, 1.5])
                                cylinder(h = 8, r = 1, $fn = 8);

                    // Tibia
                    translate([0, 0, -11])
                        rotate([110, 0, 0])
                            cylinder(h = 6, r = 0.5, $fn = 8);

                    // Tarsi
                    translate([0, 0, -15])
                        rotate([130, 0, 0])
                            cylinder(h = 4, r = 0.4, $fn = 8);
                }
    }

    hind_leg(1);
    hind_leg(-1);

    // Spines on body
    color("darkbrown")
    for (i = [0:30]) {
        angle = i * 12;
        y = 4 * sin(angle);
        z = 3 + 1.5 * cos(angle);
        translate([0, y, z])
            rotate([0, 90, 0])
                cylinder(h = 1, r = 0.2, $fn = 6);
    }
}

// Scale to printable size and position
scale([1.2, 1.2, 1.2])
    flea();

