// Firefly Model - Realistic Style, Medium Complexity
// Approx 70mm long, 3D printable

// Parameters
body_length = 40;
body_diameter = 12;
head_diameter = 10;
wing_length = 25;
wing_width = 12;
leg_length = 15;
leg_diameter = 1.2;
antenna_length = 15;
antenna_diameter = 0.8;
glow_chamber_diameter = 8;
glow_chamber_depth = 5;

module firefly() {
    // Main body segments
    union() {
        // Abdomen (glowing part)
        translate([0, 0, -body_length/2])
        cylinder(h=body_length/2, d1=body_diameter*0.8, d2=body_diameter, $fn=32);

        // Thorax
        translate([0, 0, 0])
        cylinder(h=body_length/3, d1=body_diameter*0.9, d2=body_diameter*1.1, $fn=32);

        // Head
        translate([0, 0, body_length/3])
        sphere(d=head_diameter, $fn=32);
    }

    // Glow chamber inside abdomen
    difference() {
        translate([0, 0, -body_length/2 + glow_chamber_depth/2])
        sphere(d=glow_chamber_diameter, $fn=32);
    }

    // Wings (2 pairs)
    for(side = [-1, 1]) {
        // Upper wings
        translate([side*body_diameter/2, 0, 5])
        rotate([0, side*20, -30])
        scale([1, 0.3, 0.1])
        sphere(d=wing_length, $fn=32);

        // Lower wings
        translate([side*body_diameter/2, 0, 2])
        rotate([0, side*15, -25])
        scale([1, 0.25, 0.08])
        sphere(d=wing_length*0.9, $fn=32);
    }

    // Legs (6 legs)
    for(i = [0:5]) {
        angle = i*60;
        leg_x = cos(angle)*body_diameter/2;
        leg_y = sin(angle)*body_diameter/2;

        translate([leg_x, leg_y, 0])
        rotate([90, 0, angle])
        cylinder(h=leg_length, d=leg_diameter, $fn=8);
    }

    // Antennae
    for(side = [-1, 1]) {
        translate([side*head_diameter/4, 0, body_length/3 + head_diameter/2])
        rotate([0, side*30, 0])
        cylinder(h=antenna_length, d=antenna_diameter, $fn=8);

        // Antenna tips
        translate([side*head_diameter/4 + antenna_length*sin(30), 
                  0, 
                  body_length/3 + head_diameter/2 + antenna_length*cos(30)])
        sphere(d=antenna_diameter*1.5, $fn=8);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([side*head_diameter/3, head_diameter/3, body_length/3 + head_diameter/4])
        sphere(d=head_diameter/3, $fn=16);
    }
}

// Render the firefly
firefly();

// Add print support base
translate([0, 0, -body_length/2 - 2])
cylinder(h=2, d=body_diameter*1.5, $fn=32);

