// Flying Fish - Realistic Medium Complexity
// Designed for 3D printing - approximately 80mm length

// Overall scale factor
scale_factor = 1;

// Body parameters
body_length = 40 * scale_factor;
body_height = 12 * scale_factor;
body_width = 18 * scale_factor;

// Wing parameters
wing_span = 60 * scale_factor;
wing_chord = 25 * scale_factor;
wing_thickness = 2 * scale_factor;

// Tail parameters
tail_length = 20 * scale_factor;
tail_height = 15 * scale_factor;

// Fin parameters
fin_height = 8 * scale_factor;
fin_length = 12 * scale_factor;

module fish_body() {
    // Main body - ellipsoid shape
    scale([1, body_width/body_length, body_height/body_length])
        sphere(d=body_length, $fn=32);

    // Add belly bulge
    translate([0, 0, -body_height/3])
        scale([0.8, 1, 0.5])
            sphere(d=body_length * 0.7, $fn=32);
}

module fish_head() {
    // Tapered head section
    translate([-body_length/2, 0, 0])
        rotate([0, 90, 0])
            cylinder(h=body_length/3, d1=body_width, d2=body_width*0.7, $fn=32);

    // Lower jaw
    translate([-body_length/2 - 2, 0, -body_height/3])
        rotate([0, 20, 0])
            cube([body_length/4, body_width*0.6, body_height/2], center=true);
}

module fish_wing(side) {
    // Main wing structure
    translate([0, side * body_width/2, body_height/3])
        rotate([0, side * 10, 0])
            scale([wing_chord, wing_span/2, wing_thickness])
                cube([1, 1, 1], center=true);

    // Wing struts for support
    for(i = [0:2]) {
        translate([wing_chord/4 - i*wing_chord/3, side * wing_span/4, body_height/3])
            rotate([90, 0, 0])
                cylinder(h=wing_span/2 - 5, d=1.5, center=true, $fn=8);
    }
}

module fish_tail() {
    // Upper tail lobe
    translate([body_length/2 + tail_length/2, 0, tail_height/3])
        rotate([0, 0, 15])
            scale([tail_length, tail_height/2, wing_thickness])
                cube([1, 1, 1], center=true);

    // Lower tail lobe
    translate([body_length/2 + tail_length/2, 0, -tail_height/3])
        rotate([0, 0, -15])
            scale([tail_length*0.8, tail_height/2, wing_thickness])
                cube([1, 1, 1], center=true);
}

module dorsal_fin() {
    // Main dorsal fin
    translate([body_length/4, 0, body_height/2 + fin_height/2])
        rotate([0, 0, 0])
            scale([fin_length, fin_height/2, wing_thickness])
                cube([1, 1, 1], center=true);

    // Fin rays
    for(i = [-2:2]) {
        translate([body_length/4 + i*3, 0, body_height/2 + fin_height])
            rotate([0, 90, 0])
                cylinder(h=fin_height, d=0.8, $fn=6);
    }
}

module pectoral_fin(side) {
    // Side fins
    translate([-body_length/4, side * body_width/2, -body_height/4])
        rotate([0, 90, side * 20])
            scale([fin_length/2, fin_height/2, wing_thickness])
                cube([1, 1, 1], center=true);
}

module eye(side) {
    // Eye socket
    translate([-body_length/3, side * body_width/3, body_height/4])
        sphere(d=3, $fn=16);

    // Eye ball
    translate([-body_length/3 - 0.5, side * body_width/3, body_height/4])
        sphere(d=2.5, $fn=16);
}

// Main assembly
union() {
    fish_body();
    fish_head();

    // Wings
    fish_wing(1);
    fish_wing(-1);

    fish_tail();
    dorsal_fin();

    // Side fins
    pectoral_fin(1);
    pectoral_fin(-1);

    // Eyes
    eye(1);
    eye(-1);
}

