// Flatfish (Sole/Flounder style) - Realistic Medium Complexity
// Designed for 3D printing - no overhangs, 0.8mm walls

$fn = 32;

module flatfish_body() {
    // Main body - flattened oval
    difference() {
        scale([1, 0.6, 0.15])
            sphere(d = 80);

        // Tail cutout
        translate([35, 0, -2])
            cube([25, 20, 10], center = true);
    }
}

module tail_fin() {
    // Tail fin - two lobes
    union() {
        // Left lobe
        translate([42, -12, 0])
            rotate([0, 0, -20])
            scale([1, 0.5, 0.05])
            sphere(d = 25);

        // Right lobe
        translate([42, 12, 0])
            rotate([0, 0, 20])
            scale([1, 0.5, 0.05])
            sphere(d = 25);
    }
}

module dorsal_fin() {
    // Dorsal fin along top edge
    for(i = [-30:5:25]) {
        translate([i, -22, 1])
            rotate([0, 90, 0])
            cylinder(h = 8, r1 = 3, r2 = 0.5);
    }
}

module anal_fin() {
    // Anal fin along bottom edge
    for(i = [-30:5:25]) {
        translate([i, 22, 1])
            rotate([0, 90, 0])
            cylinder(h = 8, r1 = 3, r2 = 0.5);
    }
}

module eye() {
    // Eye with socket and pupil
    union() {
        // Eye socket
        translate([-15, -12, 1.5])
            sphere(d = 8);

        // Eyeball
        translate([-15, -12, 2.5])
            sphere(d = 6);

        // Pupil
        translate([-15, -12, 3.5])
            color("black")
            sphere(d = 3);
    }
}

module mouth() {
    // Mouth opening
    translate([-25, 0, 0.5])
        rotate([90, 0, 0])
        cylinder(h = 2, r1 = 3, r2 = 2);
}

module lateral_line() {
    // Sensory lateral line
    for(i = [-20:3:20]) {
        translate([i, i * 0.2, 0.8])
            sphere(d = 1.5);
    }
}

module body_markings() {
    // Camouflage spots
    spots = [
        [-10, -8], [-5, 10], [5, -15], [15, 5], [0, 0],
        [-20, 12], [20, -10], [-25, -5], [25, 8]
    ];

    for(pos = spots) {
        translate([pos[0], pos[1], 0.5])
            scale([1, 0.8, 0.1])
            sphere(d = 4);
    }
}

module pectoral_fin() {
    // Left pectoral fin
    translate([-5, -18, 0])
        rotate([0, 0, -45])
        scale([1, 0.3, 0.05])
        sphere(d = 20);

    // Right pectoral fin (partially hidden)
    translate([-5, 18, 0])
        rotate([0, 0, 45])
        scale([1, 0.3, 0.05])
        sphere(d = 15);
}

// Assemble the fish
union() {
    flatfish_body();
    tail_fin();
    dorsal_fin();
    anal_fin();
    pectoral_fin();

    // Both eyes on same side (flatfish characteristic)
    eye();
    mirror([0, 1, 0])
        eye();

    mouth();
    lateral_line();
    body_markings();
}

