// Akita Dog - Realistic Medium Complexity
// Overall dimensions: ~80mm length x ~40mm width x ~50mm height

$fn = 30; // Increase for smoother curves

module akita() {
    // Main body
    union() {
        // Torso
        translate([0, 0, 15]) 
            scale([1.2, 0.6, 0.8]) 
                sphere(r = 20);

        // Chest
        translate([-10, 0, 10]) 
            scale([0.8, 0.5, 0.6]) 
                sphere(r = 15);

        // Hindquarters
        translate([15, 0, 12]) 
            scale([0.7, 0.5, 0.6]) 
                sphere(r = 18);
    }

    // Neck
    translate([-20, 0, 25]) 
        rotate([0, 10, 0]) 
            scale([0.5, 0.5, 0.7]) 
                cylinder(r1 = 12, r2 = 8, h = 15);

    // Head
    translate([-35, 0, 35]) 
        rotate([0, 10, 0]) {
            // Skull
            scale([0.8, 0.7, 0.7]) 
                sphere(r = 15);

            // Snout
            translate([-12, 0, -5]) 
                scale([1.2, 0.8, 0.6]) 
                    sphere(r = 8);

            // Ears
            for(side = [-1, 1]) {
                translate([-5, side * 12, 8]) 
                    rotate([0, side * 20, 0]) 
                        scale([0.3, 0.1, 0.8]) 
                            cylinder(r = 8, h = 15);
            }

            // Nose
            translate([-20, 0, -5]) 
                sphere(r = 3);
        }

    // Legs module
    module leg(x_pos, y_pos, angle) {
        translate([x_pos, y_pos, 0]) {
            // Upper leg
            rotate([0, 0, angle]) {
                translate([0, 0, 10]) 
                    rotate([0, angle, 0]) 
                        scale([0.4, 0.4, 1]) 
                            cylinder(r = 5, h = 15);

                // Lower leg
                translate([0, 0, 0]) 
                    rotate([0, angle + 10, 0]) 
                        scale([0.3, 0.3, 0.9]) 
                            cylinder(r = 4, h = 12);

                // Paw
                translate([0, 0, -2]) 
                    scale([0.8, 0.8, 0.4]) 
                        sphere(r = 5);
            }
        }
    }

    // Front legs
    leg(-15, 12, 15);
    leg(-15, -12, -15);

    // Back legs
    leg(15, 10, -15);
    leg(15, -10, 15);

    // Tail
    translate([25, 0, 25]) 
        rotate([0, 0, 45]) {
            scale([0.4, 0.4, 1.5]) 
                cylinder(r = 4, h = 20);
            translate([0, 0, 20]) 
                scale([0.8, 0.8, 0.6]) 
                    sphere(r = 4);
        }
}

// Generate the model
akita();

