// Aardwolf model
body_length = 50;
body_width = 18;
body_height = 15;
leg_height = 15;
leg_diameter = 4;
head_size = 16;
ear_height = 12;
tail_length = 35;
stripes = 5;

// Main body
translate([0, 0, body_height/2]) 
scale([body_length/body_width, 1, body_height/body_width])
sphere(d=body_width, $fn=24);

// Head
translate([body_length/2 - 2, 0, body_height/2 + 5]) {
    // Head base
    scale([1.1, 0.9, 0.9])
    sphere(d=head_size, $fn=24);

    // Snout
    translate([head_size/3, 0, -head_size/4])
    rotate([0, 15, 0])
    scale([1.4, 0.6, 0.6])
    sphere(d=head_size/2, $fn=18);

    // Ears
    for(side = [-1, 1]) {
        translate([-head_size/4, side*head_size/3, head_size/3])
        rotate([-30, 0, 30*side])
        cylinder(h=ear_height, d1=leg_diameter*1.5, d2=1, $fn=16);
    }
}

// Legs
for(x = [-1, 1], z = [-1, 1]) {
    translate([x*(body_length/3 - 3), body_width/2 - 1, -leg_height/2]) {
        // Shoulder/hip joint
        translate([0, 0, leg_height/2])
        sphere(d=leg_diameter*1.2, $fn=16);

        // Leg
        cylinder(h=leg_height, d=leg_diameter, $fn=12);

        // Foot
        translate([0, 0, -leg_diameter/2])
        scale([1.5, 1.5, 0.6])
        sphere(d=leg_diameter, $fn=12);
    }
}

// Tail
