        translate([6, 0, 6])
            rotate([0, 90, 0])
            cylinder(h=8, d1=6, d2=4, $fn=16);
    }
}

module abdomen() {
    // Abdomen segments
    for(i = [0:2]) {
        translate([14 + i*4, 0, 5 - i])
            scale([1 - i*0.1, 0.9 - i*0.05, 0.8 - i*0.1])
            sphere(d=10 - i*1.5, $fn=32);
    }
}

module head() {
    difference() {
        // Main head
        translate([-12, 0, 8])
            rotate([0, 10, 0])
            scale([1.2, 1, 0.9])
            sphere(d=10, $fn=32);

        // Eye sockets
        for(side = [-1, 1]) {
            translate([-14, side*3, 9])
                sphere(d=3, $fn=16);
        }
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([-14.5, side*3, 9])
            scale([1.1, 1, 0.8])
            sphere(d=2.5, $fn=16);
    }

    // Mandibles
    for(side = [-1, 1]) {
        translate([-16, side*1.5, 6])
            rotate([0, 0, side*20])
            scale([0.3, 0.3, 1])
            cylinder(h=6, d1=2, d2=1, $fn=8);
    }
}

// Leg segments
module leg_segment(length, angle, thickness) {
    translate([0, 0, -length/2])
        rotate([0, angle, 0])
        cylinder(h=length, d=thickness, $fn=12);
}

module leg(x_pos, side, angle) {
    translate([x_pos, side*2, 5]) {
        // Coxa
        rotate([0, 0, 90])
            leg_segment(4, 0, 2);

        // Femur
        translate([0, side*2, -2])
            rotate([0, 30, 0])
            leg_segment(6, 0, 1.5);

        // Tibia
        translate([0, side*4, -5])
            rotate([0, -30, 0])
            leg_segment(8, 0, 1);

        // Tarsus segments
        for(i = [0:2]) {
