// Parameters for printability
$fa = 2;
$fs = 0.5;
wall_thickness = 2;
scale_factor = 0.8;

module alligator() {
    // Main body
    color("darkolivegreen") {
        // Torso - main body segment
        union() {
            // Central body
            translate([-20, 0, 0]) scale([1.2, 0.8, 0.6]) sphere(d=40);

            // Tail segments
            for(i = [0:5]) {
                translate([-40 - i*8, 0, 0]) 
                rotate([0, -i*5, 0])
                scale([1 - i*0.1, 1 - i*0.15, 1 - i*0.2])
                sphere(d=30 - i*3);
            }

            // Head
            translate([15, 0, 5]) 
            scale([1.3, 1, 0.7]) 
            sphere(d=35);

            // Snout extension
            translate([30, 0, 3])
            scale([2, 0.6, 0.4])
            sphere(d=20);
        }

        // Legs
        module leg(x_pos, y_pos, angle) {
            translate([x_pos, y_pos, -15]) {
                rotate([0, 0, angle]) {
                    // Upper leg
                    rotate([90, 0, 0])
                    cylinder(h=15, d1=12, d2=8);

                    // Lower leg
                    translate([0, 0, -12])
                    rotate([90, 0, 0])
                    cylinder(h=12, d1=8, d2=6);

                    // Foot
                    translate([0, 0, -20])
                    scale([2, 1, 0.5])
                    sphere(d=8);
                }
            }
        }

        // Front legs
        leg(0, 12, -30);
        leg(0, -12, 30);

        // Rear legs
        leg(-25, 15, -45);
        leg(-25, -15, 45);

        // Eyes
        translate([28, 8, 15]) sphere(d=6);
        translate([28, -8, 15]) sphere(d=6);

        // Eye pupils
        color("black") {
            translate([31, 8, 15]) sphere(d=3);
            translate([31, -8, 15]) sphere(d=3);
        }

        // Nostrils
        translate([38, 3, 8]) sphere(d=3);
        translate([38, -3, 8]) sphere(d=3);

        // Back ridges (scutes)
        for(i = [0:12]) {
            translate([-35 + i*5, 0, 12])
            rotate([0, 0, i % 2 == 0 ? 5 : -5])
            scale([0.8, 0.2, 1])
            cube([6, 3, 4], center=true);
        }

        // Teeth
        color("white") {
            // Upper teeth
            for(i = [0:7]) {
                translate([33 - i*2, 6, 5])
                rotate([0, 0, -15])
                scale([0.3, 1, 0.3])
                cylinder(h=3, d1=2, d2=1);

                translate([33 - i*2, -6, 5])
                rotate([0, 0, 15])
                scale([0.3, 1, 0.3])
                cylinder(h=3, d1=2, d2=1);
            }
        }
    }
}

// Create the alligator
