// Aye-aye (Daubentonia madagascariensis) - medium complexity, realistic style
// Body length ~70mm, tail adds another 60mm

// Parameters
body_length = 35;
body_height = 20;
body_width = 18;
head_length = 20;
head_height = 15;
ear_size = 12;
finger_length = 18;
finger_diameter = 1.5;
leg_length = 12;
tail_length = 30;
tail_diameter = 4;

// Wall thickness for 3D printing
wall = 1.5;

module rounded_box(l, w, h, r) {
    hull() {
        translate([r, r, r]) sphere(r);
        translate([l - r, r, r]) sphere(r);
        translate([l - r, w - r, r]) sphere(r);
        translate([r, w - r, r]) sphere(r);
        translate([r, r, h - r]) sphere(r);
        translate([l - r, r, h - r]) sphere(r);
        translate([l - r, w - r, h - r]) sphere(r);
        translate([r, w - r, h - r]) sphere(r);
    }
}

module body() {
    union() {
        // Main torso
        translate([0, 0, 0])
            rounded_box(body_length, body_width, body_height, 5);

        // Rounded back
        translate([body_length/2, 0, body_height/2])
            scale([1.2, 1.1, 0.8])
                sphere(body_height/2);
    }
}

module head() {
    translate([body_length, 0, body_height/2]) {
        rotate([0, -10, 0]) {
            // Skull shape
            difference() {
                hull() {
                    sphere(head_height/2);
                    translate([head_length - head_height/2, 0, 0])
                        sphere(head_height/2);
                }

                // Eye sockets
                translate([head_length - 5, -4, 3])
                    sphere(2.5);
                translate([head_length - 5, 4, 3])
                    sphere(2.5);
            }

            // Large ears
            translate([-2, -ear_size/2, head_height/2])
                rotate([0, 0, -30])
                    scale([0.8, 2, 0.3])
                        sphere(ear_size/2);
            translate([-2, ear_size/2, head_height/2])
                rotate([0, 0, 30])
                    scale([0.8, 2, 0.3])
                        sphere(ear_size/2);

            // Long middle finger
            translate([head_length + 2, 0, -2])
                rotate([0, 90, 0])
                    cylinder(h = finger_length, d = finger_diameter, $fn = 8);
        }
    }
}

module limbs() {
    // Front legs
    for(side = [-1, 1]) {
        translate([body_length/2 - 5, side * body_width/2, 0])
            rotate([0, 0, side * 30])
                cylinder(h = leg_length, d = 4, $fn = 8);
    }

    // Hind legs
    for(side = [-1, 1]) {
        translate([5, side * body_width/2 + 2, 0])
            rotate([0, 0, side * 45])
                cylinder(h = leg_length + 3, d = 5, $fn = 8);
    }
}

module tail() {
    translate([0, 0, body_height/2 - 2])
        rotate([0, 90, 0])
            cylinder(h = tail_length, d1 = tail_diameter, d2 = 2, $fn = 12);

    // Tail fur texture
    for(i = [0:3:tail_length]) {
        translate([0, 0, i])
            rotate([0, 90, 0])
                cylinder(h = 3, d = tail_diameter * 1.3, $fn = 8);
    }
}

// Assemble the aye-aye
union() {
    body();
    head();
    limbs();
    tail();
}

