// Baboon model - medium complexity realistic style
// Overall size approximately 80mm tall

// Main dimensions
body_h = 35;
body_w = 25;
body_d = 30;
head_r = 12;
muzzle_l = 8;
muzzle_w = 6;
muzzle_h = 5;
leg_h = 20;
leg_w = 7;
arm_l = 18;
arm_w = 5;
tail_l = 30;
tail_seg = 5;

module body() {
    // Main torso
    difference() {
        translate([0, 0, leg_h + body_h/2])
            scale([1, 0.8, 1.2])
            sphere(d=body_w);

        // Chest indentation
        translate([0, body_d/2, leg_h + body_h/2])
            rotate([90, 0, 0])
            cylinder(h=body_d, d1=body_w*0.6, d2=0, $fn=20);
    }

    // Back arch
    translate([0, -body_d/3, leg_h + body_h/2 + 5])
        rotate([10, 0, 0])
        cube([body_w*0.8, body_d*0.6, 8], center=true);
}

module head() {
    // Main head sphere
    translate([0, body_d/2 + head_r*0.7, leg_h + body_h + head_r*0.8])
        sphere(r=head_r);

    // Muzzle
    translate([0, body_d/2 + head_r + muzzle_l/2, leg_h + body_h + head_r*0.7])
        scale([1, 1.5, 0.8])
        cube([muzzle_w, muzzle_l, muzzle_h], center=true);

    // Nose
    translate([0, body_d/2 + head_r + muzzle_l, leg_h + body_h + head_r*0.7])
        sphere(d=3);

    // Eyes
    for(x = [-head_r*0.4, head_r*0.4]) {
        translate([x, body_d/2 + head_r*0.8, leg_h + body_h + head_r*0.9])
            sphere(d=4);
        translate([x*1.1, body_d/2 + head_r*0.9, leg_h + body_h + head_r*0.9])
            sphere(d=2);
    }

    // Ears
    for(x = [-head_r*0.8, head_r*0.8]) {
        translate([x, body_d/2 + head_r*0.3, leg_h + body_h + head_r*1.2])
            rotate([0, 10, 0])
            scale([0.8, 0.5, 1])
            sphere(d=6);
    }
}

module limbs() {
    // Legs with feet
    for(side = [-1, 1]) {
        // Leg
        translate([side * body_w/3, 0, leg_h/2])
            cylinder(h=leg_h, d=leg_w);

        // Foot
        translate([side * body_w/3, body_d/4, 2])
            scale([1.2, 2, 0.6])
            cube([leg_w, leg_w*1.5, leg_w], center=true);
    }

    // Arms with hands
    for(side = [-1, 1]) {
        // Shoulder
        translate([side * body_w/2, body_d/3, leg_h + body_h - 5])
            rotate([0, 0, side * -30])
            cylinder(h=arm_l, d=arm_w);

        // Hand
        translate([side * (body_w/2 + arm_l*0.8), body_d/3 + 2, leg_h + body_h - 8])
            rotate([0, 90, 0])
            scale([1.2, 1, 0.8])
            sphere(d=arm_w*1.2);
    }
}

module tail() {
    // Segmented tail with gradual curve
    for(i = [0:5]) {
        rotate([0, 0, -i*15])
        translate([0, -body_d/2 - i*tail_seg, leg_h - i*2])
            rotate([0, 90, 0])
            cylinder(h=tail_seg, d=tail_seg - i*0.5);
    }
}

// Assemble the baboon
union() {
    body();
    head();
    limbs();
