    for(side = [-1, 1]) {
        translate([-5, side*12, 8]) rotate([10,0,side*15]) 
            cylinder(h=15, r1=5, r2=4);
        translate([-5, side*12, -6]) rotate([0,side*20,0]) 
            scale([1.5,0.8,0.5]) sphere(r=4);
