// Avocet 3D Model - Medium Complexity, Realistic Style
// Based on pied avocet (Recurvirostra avosetta)

$fn = 32; // Smooth curves for 3D printing

// Main body parameters
body_length = 60;
body_height = 25;
body_width = 15;

// Wing parameters
wing_length = 35;
wing_width = 12;
wing_thickness = 1.5;

// Head parameters
head_length = 20;
head_height = 12;
beak_length = 35;
beak_height = 3;

// Leg parameters
leg_height = 35;
leg_thickness = 2;
foot_length = 8;
foot_width = 3;

// Module for the main body
module avocet_body() {
    // Central body - elliptical shape
    scale([1, 0.7, 0.5])
    sphere(d=body_length);

    // Tail extension
    translate([-body_length/2 - 5, 0, 0])
    scale([1.5, 0.3, 0.2])
    sphere(d=10);

    // Back ridge
    translate([0, 0, body_height/4])
    rotate([90, 0, 0])
    cylinder(h=body_length/1.5, r1=2, r2=0.5);
}

// Module for the head and beak
module avocet_head() {
    // Head
    translate([body_length/2 + head_length/2, 0, body_height/2])
    scale([1.2, 0.9, 0.9])
    sphere(d=head_length);

    // Beak - curved upward
    translate([body_length/2 + head_length + beak_length/2, 0, body_height/2])
    rotate([0, -10, 0])
    scale([beak_length, beak_height, beak_height])
    cube([1, 1, 1], center=true);

    // Beak tip
    translate([body_length/2 + head_length + beak_length, 0, body_height/2])
    sphere(d=beak_height);

    // Eye
    translate([body_length/2 + head_length/2 - 3, body_height/4, body_height/2])
    sphere(d=2);
}

// Module for wings
module avocet_wings() {
    // Left wing
    translate([-5, body_width/2 + wing_width/2, body_height/3])
    rotate([0, 0, -20])
    scale([1, 0.3, 0.1])
    difference() {
        cube([wing_length, wing_width, wing_thickness]);
        // Wing tip curve
        translate([wing_length - 5, wing_width/2, 0])
        sphere(d=10);
    }

    // Right wing (mirrored)
    mirror([0, 1, 0])
    translate([-5, body_width/2 + wing_width/2, body_height/3])
    rotate([0, 0, -20])
    scale([1, 0.3, 0.1])
    difference() {
        cube([wing_length, wing_width, wing_thickness]);
        translate([wing_length - 5, wing_width/2, 0])
        sphere(d=10);
    }
}

// Module for legs and feet
module avocet_legs() {
    // Left leg
    translate([body_length/4, body_width/4, -body_height/2 - leg_height/2])
    cylinder(h=leg_height, r=leg_thickness);

    // Left foot
    translate([body_length/4 + foot_length/2, body_width/4 + foot_width/2, -body_height/2 - leg_height])
    cube([foot_length, foot_width, 2]);

    // Right leg
    translate([body_length/4, -body_width/4, -body_height/2 - leg_height/2])
    cylinder(h=leg_height, r=leg_thickness);

    // Right foot
    translate([body_length/4 + foot_length/2, -body_width/4 - foot_width/2, -body_height/2 - leg_height])
    cube([foot_length, foot_width, 2]);
}

// Module for neck
module avocet_neck() {
    translate([body_length/2 - 5, 0, 0])
    rotate([0, -20, 0])
    cylinder(h=15, r1=body_width/2, r2=body_width/3);
}

// Assemble all parts
union() {
    // Main body
    avocet_body();

    // Neck
    avocet_neck();

    // Head and beak
    avocet_head();

    // Wings
    avocet_wings();

    // Legs and feet
    avocet_legs();
}

