// Medium-complexity realistic adder (snake) – 3D printable
// Overall length ≈ 90 mm, lays flat on the print bed
// All bridges ≤ 45° or supported by solid material

// Basic parameters
$fn = 32;               // smooth curves
body_len = 90;          // total length
head_len = 18;
head_w   = 12;
body_rad = 6;           // radius of body cross-section
scale_taper = 0.7;      // tail taper ratio
segment_len = 8;        // length of one body segment
wall = 1.2;             // printable wall thickness
eye_d = 3;
eye_ofs = 4;

// Color definitions (for preview only)
head_col = "darkolivegreen";
body_col = "olivedrab";

// Helper: create one body segment with overlapping spheres for smooth joints
module body_segment(idx, total) {
    // progressive scaling toward tail
    t = idx / (total - 1);
    sc = 1 - t * (1 - scale_taper);
    translate([idx * segment_len * sc, 0, 0])
        scale([sc, sc, sc])
        sphere(d = body_rad * 2);
}

// Head with snout, eyes and forked tongue
module head() {
    color(head_col)
    union() {
        // Main head block
        translate([0, 0, body_rad])
            rotate([0, -10, 0])   // slight lift of snout
            difference() {
                hull() {
                    sphere(d = head_w);
                    translate([head_len - head_w/2, 0, 0])
                        sphere(d = head_w * 0.7);
                }
                // Eye sockets
                for(side = [-1, 1])
                    translate([head_len/3, side * eye_ofs, head_w/2 - 1])
                        rotate([0, 90, 0])
                        cylinder(h = eye_d + 2, d = eye_d, center = true);
            }

        // Eyes
        for(side = [-1, 1])
            translate([head_len/3, side * eye_ofs, head_w/2 + 0.5])
                color("black")
                sphere(d = eye_d);

        // Forked tongue
        difference() {
            union() {
                translate([head_len, 0, 0])
                    rotate([0, -30, 0])
                    cube([6, 0.8, 0.8], center = true);
                translate([head_len + 3, 0, 0])
                    rotate([0, -30, 0])
                    cube([4, 0.8, 0.4], center = true);
            }
            // split tongue
            translate([head_len + 3.5, 0, 0])
                cube([2, 2, 0.5], center = true);
        }
    }
}

// Scaled belly plate to ensure flat bottom
module belly_plate() {
    color("darkgreen")
    linear_extrude(height = wall)
    offset(delta = wall)
    projection(cut = true)
    children();
}

// Main assembly
union() {
    // Head
    translate([0, 0, body_rad])
        head();

    // Body segments
    color(body_col) {
        segments = floor(body_len / segment_len);
        for(i = [1 : segments])
            body_segment(i, segments);
    }

    // Flattened belly to avoid support
    translate([0, 0, -body_rad])
        belly_plate() {
            union() {
                // Head underside
                translate([head_len/2, 0, body_rad])
                    scale([head_len, head_w, 1])
                    sphere(d = 2);

                // Body underside
                segments = floor(body_len / segment_len);
                for(i = [0 : segments])
                    body_segment(i, segments);
            }
        }
}

