$fn = 32;  // Smooth curves for realistic appearance
body_length = 80;
body_height = 20;
body_width = 15;

module barracuda() {
    // Main body - elongated teardrop shape using hull
    hull() {
        translate([-body_length/2, 0, 0]) 
            sphere(d=body_width*0.8);
        translate([body_length/2-5, 0, 0]) 
            sphere(d=body_width*0.3);
    }

    // Upper jaw extension
    translate([body_length/2-8, 0, body_height/3])
        rotate([0, -20, 0])
        scale([1.2, 0.8, 0.6])
        cylinder(h=15, d1=body_width*0.4, d2=body_width*0.2);

    // Lower jaw
    translate([body_length/2-10, 0, -body_height/3])
        rotate([0, 15, 0])
        scale([1, 0.7, 0.5])
        cylinder(h=12, d1=body_width*0.3, d2=body_width*0.15);

    // Dorsal fin - multiple segments
    for(i=[-10:5:15]) {
        translate([i*2, 0, body_height/2])
            rotate([0, 20-i, 0])
            scale([0.8, 0.1, 1])
            cylinder(h=body_height*0.8, d1=body_width*0.3, d2=body_width*0.1);
    }

    // Pectoral fins
    module pectoral_fin(side) {
        translate([body_length/4, side*body_width/2, -body_height/4])
            rotate([0, 0, side*30])
            scale([1, 0.3, 0.7])
            cylinder(h=body_width*0.8, d1=body_width*0.4, d2=body_width*0.1);
    }
    pectoral_fin(1);
    pectoral_fin(-1);

    // Tail fin - forked caudal fin
    module tail_fin() {
        translate([-body_length/2+2, 0, 0])
            rotate([0, 0, 0])
            difference() {
                // Upper lobe
                translate([0, 0, body_height/3])
                    rotate([0, 0, 20])
                    scale([0.5, 0.2, 1.5])
                    cylinder(h=body_height*1.2, d1=body_width*0.5, d2=body_width*0.2);

                // Lower lobe
                translate([0, 0, -body_height/3])
                    rotate([0, 0, -20])
                    scale([0.5, 0.2, 1.5])
                    cylinder(h=body_height*1.2, d1=body_width*0.5, d2=body_width*0.2);
            }
    }
    tail_fin();

    // Eyes
    module eye(side) {
        translate([body_length/2-15, side*body_width/3, body_height/4])
            sphere(d=body_width*0.15);
        translate([body_length/2-14.5, side*body_width/3, body_height/4])
            sphere(d=body_width*0.07);  // Pupil
    }
    eye(1);
    eye(-1);

    // Gill slits
    for(i=[1:3]) {
        translate([body_length/2-25-i*3, body_width/2, -body_height/4])
            rotate([0, 90, 0])
