        translate([0, body_width/2 - 5, body_height/4])
        rotate([90, 0, 0])
        cylinder(h=body_width, d1=12, d2=8, center=true);

        // Tail opening
        translate([-body_length/2 + 5, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=15, d1=10, d2=8, center=true);
    }
}

// Head module
module head() {
    translate([0, body_width/2 - 3, body_height/3 + 5])
    union() {
        // Main head shape
        scale([0.9, 1, 1.1])
        sphere(d=15);

        // Beak
        translate([0, 8, -2])
        rotate([30, 0, 0])
        scale([0.3, 0.4, 1])
        cylinder(h=10, d1=8, d2=0);

        // Eyes
        for(x = [-4, 4]) {
            translate([x, 5, 2])
            color("black")
            sphere(d=3);
        }
    }
}

