module wing(is_left = true) {
    side = is_left ? 1 : -1;
    translate([side * body_width/4, 0, body_height/3])
    rotate([0, 0, is_left ? 30 : -30])
    scale([0.8, 2, 0.2])
    sphere(d=body_width);
}

