// Bushbaby (Galago) - Medium complexity realistic style
// Designed for 3D printing - no overhangs, 60mm height

// Main parameters
body_length = 30;
body_height = 25;
head_size = 15;
ear_size = 8;
tail_length = 40;
limb_length = 22;

// Body module
module body() {
    // Main torso
    translate([0, 0, body_height/2])
    scale([1.2, 0.8, 1])
    sphere(d=body_length, $fn=32);

    // Chest bulge
    translate([body_length/3, 0, body_height/2-5])
    scale([0.8, 0.6, 0.7])
    sphere(d=body_length*0.6, $fn=24);
}

// Head module
module head() {
    translate([-body_length/2 - head_size/2, 0, body_height + head_size/2 - 5])
    union() {
        // Main head sphere
        sphere(d=head_size, $fn=32);

        // Muzzle
        translate([-head_size/2, 0, -head_size/4])
        scale([1.2, 0.7, 0.6])
        sphere(d=head_size*0.7, $fn=24);

        // Eyes
        for(x = [-head_size/3, head_size/3]) {
            translate([x, head_size/2 - 2, head_size/4])
            sphere(d=3, $fn=16);

            // Eye shine
            translate([x+0.5, head_size/2 - 1.5, head_size/4+1])
            sphere(d=1, $fn=8);
        }

        // Nose
        translate([-head_size/2 - 2, 0, 0])
        sphere(d=2, $fn=8);
    }
}

// Ear module
module ear(side) {
    translate([-body_length/2 - head_size/2, side*(head_size/2 + 2), body_height + head_size/2 + 5])
    rotate([0, 15*side, 0])
    union() {
        // Outer ear
        scale([0.3, 1, 1.5])
        sphere(d=ear_size, $fn=16);

        // Inner ear detail
        translate([0, side*0.5, -1])
        scale([0.2, 0.8, 1.2])
        sphere(d=ear_size*0.7, $fn=16);
    }
}

// Limb module
module limb(position, angle) {
    translate([position[0], position[1], position[2]])
    rotate([0, angle, 0])
    union() {
        // Upper arm/leg
        translate([0, 0, -limb_length/4])
        cylinder(h=limb_length/2, d1=4, d2=3, $fn=12);

        // Lower arm/leg
        translate([0, 0, -limb_length*3/4])
