// Realistic Bug - Medium Complexity
// Dimensions: ~60mm long, ~40mm wide, ~25mm high

// Body segments
module thorax() {
    // Main thorax segment
    difference() {
        scale([1.2, 0.8, 0.6]) sphere(d=20);
        // Neck cavity
        translate([10, 0, 0]) rotate([0, 90, 0]) 
            cylinder(h=15, d=8, $fn=20);
        // Abdomen cavity
        translate([-10, 0, 0]) rotate([0, 90, 0]) 
            cylinder(h=15, d=10, $fn=20);
    }
}

module abdomen() {
    // Abdomen segment with segments
    union() {
        // Main abdomen
        translate([-25, 0, -2]) scale([1.5, 1, 0.8]) sphere(d=18);

        // Segmented rings
        for(i = [1:4]) {
            translate([-28 + i*5, 0, 0]) 
                rotate([90, 0, 0]) 
                cylinder(h=2, d1=18-i*2, d2=16-i*2, $fn=32);
        }
    }
}

module head() {
    // Head with compound eyes
    union() {
        // Main head
        translate([15, 0, 2]) scale([1, 0.9, 0.7]) sphere(d=16);

        // Compound eyes
        for(side = [-1, 1]) {
            translate([18, side*6, 4]) 
                sphere(d=8);
            // Eye facets
            for(phi = [0:30:150]) {
                for(theta = [0:30:150]) {
                    translate([18 + cos(phi)*3, side*6 + sin(phi)*3, 4 + sin(theta)*2]) 
                        sphere(d=1);
                }
            }
        }

        // Mandibles
        for(side = [-1, 1]) {
            translate([20, side*4, -2]) 
                rotate([0, 0, side*20]) 
                cylinder(h=8, d1=3, d2=1, $fn=8);
        }
    }
}

// Leg segments
module leg_segment(length, thickness) {
    cylinder(h=length, d=thickness, $fn=8);
}

module leg(side, segment, angle) {
    // Position legs around body
    rotate([0, 0, angle]) {
        translate([0, side*12, -5]) {
            // Coxa
            rotate([90, 0, 0]) 
                leg_segment(8, 4);

            // Femur
            translate([0, 0, 8]) 
                rotate([30*side, 0, 0]) 
                leg_segment(12, 3);

            // Tibia
            translate([0, -10*side, 12]) 
                rotate([60*side, 0, 0]) 
                leg_segment(10, 2);

            // Tarsus
            translate([0, -18*side, 18]) 
                rotate([90*side, 0, 0]) 
                leg_segment(8, 1.5);
        }
    }
}

// Wings (elytra)
module wing(side) {
    translate([0, side*8, 5]) {
        rotate([0, 30, 0]) {
            // Main wing
            translate([-5, 0, 0]) 
                scale([1.2, 0.3, 0.05]) 
                sphere(d=25);

            // Wing veins
            for(i = [0:3]) {
                translate([-5 + i*8, 0, 0]) 
                    rotate([0, 90, 0]) 
                    cylinder(h=25, d=0.5, $fn=4);
            }
        }
    }
}

// Antennae
module antenna(side) {
    translate([22, side*4, 8]) {
        rotate([0, 30*side, 0]) {
            // Main antenna
            cylinder(h=15, d=1, $fn=8);

            // Antennal segments
            for(i = [1:3]) {
                translate([0, 0, i*5]) 
                    sphere(d=1.5);
            }
        }
    }
}

// Main assembly
union() {
    // Body parts
    thorax();
    abdomen();
    head();

    // 6 legs - 3 per side
    for(side = [-1, 1]) {
        for(i = [0:2]) {
            leg(side, i, -30 + i*30);
        }
    }

    // Wings
    wing(1);
    wing(-1);

    // Antennae
    antenna(1);
    antenna(-1);
}

