// Cassowary - Medium complexity realistic style
// 3D printable model using basic primitives

$fn = 32;  // Smooth curves for printing

// Main module
module cassowary() {
    // Scale to ~80mm height
    scale(0.8) {
        union() {
            // Body
            body();

            // Head and neck
            translate([0, 20, 35]) rotate([0, 10, 0]) head_and_neck();

            // Legs
            translate([-12, -25, 0]) leg(true);
            translate([12, -25, 0]) leg(false);

            // Tail
            translate([0, -35, 5]) tail();
        }
    }
}

// Main body
module body() {
    difference() {
        // Main torso
        rotate([90, 0, 0]) scale([1.2, 1, 1.5]) cylinder(h=40, r1=15, r2=12);

        // Neck hole
        translate([0, 20, 35]) rotate([0, 10, 0]) cylinder(h=20, r=6);
    }

    // Back feathers (casque-like structures)
    for(i = [-2:1:2]) {
        translate([i*8, -5, 45]) rotate([0, -10, 0]) 
            scale([0.8, 0.8, 1]) cylinder(h=8, r=4);
    }
}

// Head and neck assembly
module head_and_neck() {
    union() {
        // Neck
        color("dimgray") 
            rotate([-10, 0, 0]) cylinder(h=25, r1=6, r2=4);

        // Head
        translate([0, 0, 25]) {
            // Main head shape
            scale([1.2, 1, 0.8]) sphere(r=8);

            // Beak
            translate([0, 12, -2]) rotate([20, 0, 0])
                scale([0.4, 2, 0.3]) cylinder(h=10, r=5);

            // Casque (helmet)
            translate([0, 0, 6]) rotate([30, 0, 0])
                scale([0.8, 1.5, 0.5]) cylinder(h=12, r=5);

            // Eyes
            translate([3, 5, 2]) sphere(r=1.5);
            translate([-3, 5, 2]) sphere(r=1.5);

            // Wattles
            color("red") {
                translate([4, 8, -3]) sphere(r=2);
                translate([-4, 8, -3]) sphere(r=2);
            }
        }
    }
}

// Leg module
module leg(left) {
    mirror([left ? 0 : 1, 0, 0]) {
        // Upper leg
        color("dimgray")
            translate([0, 0, 15]) rotate([10, 0, 10]) 
                cylinder(h=20, r=4);

        // Lower leg
        translate([2, 0, -5]) rotate([20, 0, 10])
            cylinder(h=25, r=3);

        // Foot
        translate([4, 0, -30]) {
            // Main foot
            scale([2, 1, 0.5]) sphere(r=5);

            // Toes
            for(angle = [0:20:40]) {
                rotate([0, 0, angle]) 
                    translate([8, 0, 0]) scale([1, 0.3, 0.3]) 
                        cylinder(h=8, r=2);
