// Caribou (Reindeer) Model
// Created using basic primitives and transformations

// Main body
module body() {
    translate([0, 0, 30]) 
    scale([1.5, 0.7, 1.2]) 
    sphere(d=40, $fn=24);
}

// Head
module head() {
    translate([35, 0, 45]) 
    rotate([0, -10, 0]) 
    scale([1, 0.8, 1]) 
    sphere(d=30, $fn=24);
}

// Legs
module leg() {
    translate([0, 0, 15]) 
    cylinder(h=30, d1=5, d2=3, $fn=16);
}

module legs() {
    // Front legs
    translate([15, -10, 0]) leg();
    translate([15, 10, 0]) leg();

    // Hind legs
    translate([-15, -10, 0]) leg();
    translate([-15, 10, 0]) leg();
}

// Neck
module neck() {
    translate([20, 0, 40]) 
    rotate([0, -10, 0]) 
    scale([0.8, 0.6, 1.2]) 
    cylinder(h=20, d1=12, d2=10, $fn=20);
}

// Tail
module tail() {
    translate([-35, 0, 35]) 
    rotate([0, 20, 0]) 
    scale([0.5, 0.5, 1.5]) 
    cylinder(h=15, d1=4, d2=1, $fn=12);
}

// Antler branch
module antler_segment(length, angle, end_radius) {
    rotate([0, angle, 0]) 
    cylinder(h=length, d1=end_radius*2, d2=end_radius, $fn=10);
    translate([0, 0, length]) 
    sphere(d=end_radius*2, $fn=10);
}

// Full antler
module antler() {
    // Main beam
    antler_segment(25, 70, 1.5);

    // First tine
    translate([0, 0, 15]) 
    rotate([0, 30, 45]) 
    antler_segment(12, 10, 1);

    // Second tine
    translate([0, 0, 20]) 
    rotate([0, -40, -30]) 
    antler_segment(10, 10, 1);

    // Third tine
    translate([0, 0, 10]) 
    rotate([0, 20, -20]) 
    antler_segment(8, 10, 1);
}

// Antlers
module antlers() {
    // Left antler
    translate([40, -8, 60]) 
