// Chamois (Rupicapra rupicapra) – medium-complexity realistic model
// 3D-printable, no overhangs ≥45°, wall thickness 1.2 mm

module rounded_cylinder(h, r1, r2, vertical=true) {
    // Helper: cylinder with half-sphere ends for organic joints
    $fn = 16;
    if (vertical) {
        translate([0,0,h/2]) cylinder(h=h, r1=r1, r2=r2);
        translate([0,0,h]) sphere(r2);
        translate([0,0,0]) sphere(r1);
    } else {
        rotate([0,90,0]) rounded_cylinder(h, r1, r2, vertical=true);
    }
}

module leg(x, y, angle, scale=1) {
    translate([x,y,0]) {
        rotate([0,0,angle]) {
            scale([scale,scale,scale]) {
                // Upper leg
                rounded_cylinder(10, 3.2, 2.2);
                // Lower leg
                translate([0,0,10]) rotate([10,0,0]) rounded_cylinder(12, 2.2, 1.8);
                // Hoof
                translate([0,0,22]) cylinder(h=4, r1=1.8, r2=2.2, $fn=8);
            }
        }
    }
}

module horn() {
    // Curved backward horn
    for (i=[0:3]) {
        rotate([0,0,-i*8]) translate([0,i*0.8,0]) rotate([15,0,0]) 
            scale([1,1,0.8]) cylinder(h=2, r1=5-i*0.3, r2=4.7-i*0.3, $fn=6);
    }
    translate([0,0,8]) sphere(4, $fn=8);
}

module ear() {
    rotate([0,20,0]) scale([0.6,1,1]) sphere(4, $fn=12);
}

module tail() {
    translate([0,-18,4]) rotate([30,0,0]) scale([0.8,1,1.2]) sphere(3, $fn=10);
}

difference() {
    // Main body
    union() {
        // Torso
        scale([1,1.5,1]) sphere(12, $fn=32);

        // Neck
        translate([0,8,8]) rotate([-30,0,0]) rounded_cylinder(15, 5, 3.5);

        // Head
        translate([0,18,10]) rotate([-20,0,0]) scale([0.7,1.2,0.8]) sphere(8, $fn=20);

        // Rump
        translate([0,-10,0]) sphere(10, $fn=20);

        // Legs
        leg(6, 4, 30, 1);   // front right
        leg(-6, 4, -30, 1); // front left
        leg(5, -8, 25, 0.9); // rear right
        leg(-5, -8, -25, 0.9); // rear left

        // Horns
        translate([3,18,14]) horn();
        translate([-3,18,14]) mirror([1,0,0]) horn();

        // Ears
        translate([4,19,16]) ear();
        translate([-4,19,16]) mirror([1,0,0]) ear();

        // Tail
        tail();
    }

    // Eye sockets
    translate([2.5,21,11]) rotate([0,0,-15]) sphere(1.5, $fn=8);
    translate([-2.5,21,11]) rotate([0,0,15]) sphere(1.5, $fn=8);

