// Overall dimensions
wing_span = 70;
body_length = 40;
body_diameter = 6;

module wing_base() {
    difference() {
        // Main wing shape
        scale([1, 0.7, 0.2])
        sphere(r = wing_span / 2);

        // Remove bottom half
        translate([0, 0, -wing_span / 2])
        cube([wing_span * 2, wing_span * 2, wing_span], center = true);
    }
