module wing_detail() {
    // Vein pattern
    for (i = [0:30:150]) {
        rotate([0, 0, i])
        translate([0, 0, 0.5])
        scale([0.02, 1, 0.05])
        cylinder(h = wing_span * 0.4, r1 = 0, r2 = 1);
    }

    // Wing spots
    for (pos = [[-10, 5], [-15, 0], [-12, -5]]) {
        translate([pos[0], pos[1], 0.6])
        scale([1, 0.7, 0.05])
        sphere(r = 3);
    }
}

module left_wing() {
    translate([-body_diameter, 0, 0])
    union() {
        wing_base();
        wing_detail();
    }
}

module body_segment(height, radius, offset) {
    translate([0, 0, offset])
    cylinder(h = height, r = radius, $fn = 12);
